(function(a){a.ajax({type:"get",url:"php/getGoodsList.php",success:function(a){showgoodsList(a)},dataType:"json"})})(jQuery);function showgoodsList(a){let b=document.getElementById("ulbox");for(let c,d=0;d<a.length;d++)c="<div class='detail_demo'><a href='javascript:;'><img src='"+a[d].goodsImg+"' width='200' height='200' /></a><a href='javascript:;' class='word'>"+a[d].goodsDesc+"</a><i>"+a[d].goodsPrice+"<span>"+a[d].beiyong1+"</span></i><p><a class='red' href='javascript:;'>"+a[d].beiyong2+"</a><a class='nothing' href='javascript:;'>"+a[d].beiyong3+"</a></p></div>",b.append(c)}